package firstproject1;

public class WrapperClasses {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
        int aa = 50;  int bb;
        Integer obj= new Integer(aa);
        System.out.println(obj);
        
        bb= obj;        
        System.out.println(bb);
        
       float  cc = 60.7f;  float dd;
       Float obj1 = new Float(cc);
       System.out.println(obj1);
        
       dd = obj1;
       System.out.println(dd);
        
       boolean ee = true; boolean ff;
       Boolean obj2 = new Boolean(ee);
       System.out.println(ee);
        
       ff= obj2;
       System.out.println(ff);
       
       byte gg = 50;  byte hh;
       Byte obj3= new Byte(gg);
       System.out.println(obj3);
       
       hh= obj3;
       System.out.println(hh);
       
       long ii = 3546869;  long jj;
       Long obj4= new Long(ii);
       System.out.println(obj4);
        
       jj=obj4;
       System.out.println(jj);
       
       short kk = 234; short ll;
       Short obj5 = new Short(kk);
       System.out.println(kk);
       
       ll = obj5;
       System.out.println(ll);
       
       double mm = 79.757; double nn;
       Double obj6 = new Double(mm);
       System.out.println(mm);
       
       nn = obj6;
       System.out.println(nn);
       
       char oo = 'c'; char pp;
       Character obj7 = new Character(oo);
       System.out.println(oo);
       
       pp = obj7;
       System.out.println(pp);
	}

}
